    
    function clock() {
        const now = new Date();
        const options = { timeZone: 'Europe/Warsaw', hour: 'numeric', minute: 'numeric', second: 'numeric' };
        const formattedTime = now.toLocaleTimeString('pl-PL', options);
        $('#clock').text(formattedTime);
    }

    setInterval(clock, 1000);
